let magazine = prompt("Magazine: Dinero / National Geographic / American Journal").toLowerCase();
let time = prompt("Time: 3 months / 6 months / 1 year").toLowerCase();
let operatorName = prompt("Mobile operator:").toLowerCase();

let subCost = 0;

switch (magazine) {
  case "dinero":
    switch (time) {
      case "3 months": subCost = 6; break;
      case "6 months": subCost = 11; break;
      case "1 year": subCost = 20; break;
      default: subCost = 0; break;
    }
    break;
  case "national geographic":
    switch (time) {
      case "3 months": subCost = 10; break;
      case "6 months": subCost = 13; break;
      case "1 year": subCost = 22; break;
      default: subCost = 0; break;
    }
    break;
  case "american journal":
    switch (time) {
      case "3 months": subCost = 12; break;
      case "6 months": subCost = 18; break;
      case "1 year": subCost = 30; break;
      default: subCost = 0; break;
    }
    break;
  default:
    subCost = 0;
}

switch (operatorName === "claro" ? "claro" : "other") {
  case "claro":
    subCost = subCost * 0.95;
    break;
  default:
    // no discount
}

alert("Total to pay: " + subCost + " USD");
