let power = prompt("Traffic light: ON or OFF?").toLowerCase();
let color = "";

switch (power) {
  case "off":
    alert("Traffic light is OFF");
    break;
  case "on":
    color = prompt("Color: green / yellow / red").toLowerCase();
    switch (color) {
      case "green": alert("You may go"); break;
      case "yellow": alert("Caution"); break;
      case "red": alert("Stop"); break;
      default: alert("Device error"); break;
    }
    break;
  default:
    alert("Invalid state");
}
